mapper.update:
UPDATE cockpit_user
SET status_id = 2
WHERE id = #{id}

UPDATE cockpit_user
SET status_id = 3
WHERE id = #{id}

mapper.select:
SELECT u.id AS uid, u.username, u.password, u.email, u.status_id, t.id AS tid, t.name FROM cockpit_user AS u JOIN team_user_xref AS xref ON u.id = xref.user_id JOIN team AS t on xref.team_id = t.id WHERE 1 = 1
AND u.id = #{id}
AND u.username = #{userName}
